CREATE TABLE `rss_user_collect` (
  `uid` mediumint(8) unsigned NOT NULL,
  `feed_id` int(20) unsigned NOT NULL,
  `collect_title` varchar(50) DEFAULT NULL,
  `collect_url` varchar(512) NOT NULL,
  `collect_summary` text NOT NULL,
